package chap03;	//비트 논리 연산자

public class BitLogicExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("45 & 25 = "+(45 & 25));
		System.out.println("45 | 25 = "+(45 | 25));
		System.out.println("45 ^ 25 = "+(45 ^ 25));
		System.out.println("~45 = "+(~45));
	}

}
